def add_permissions(apps, schema_editor):
    """
    Add permissions for Request model.
    :param apps:
    :param schema_editor:
    :return:
    """
    permission = apps.get_model("auth", "Permission")
    content_type = apps.get_model("contenttypes", "ContentType")
    
    model_def = apps.get_model("aoi", "Request")
    
    # Content type objects
    model_content_type = content_type.objects.get_for_model(model_def)
    
    db_alias = schema_editor.connection.alias
    
    permissions_data = [
        {"codename": "add_request", "name": "Can add Request",
         "content_type": model_content_type},
        {"codename": "change_request", "name": "Can change Request",
         "content_type": model_content_type},
        {"codename": "delete_request", "name": "Can delete Request",
         "content_type": model_content_type},
        {"codename": "view_request", "name": "Can view Request",
         "content_type": model_content_type},
        {"codename": "add_another_user_request", "name": "Can add Request for another user",
         "content_type": model_content_type},
    ]
    
    permission_list = []
    for permission_data in permissions_data:
        if not permission.objects.filter(codename=permission_data["codename"]).exists():
            permission_list.append(permission(codename=permission_data["codename"], name=permission_data["name"],
                                              content_type=permission_data["content_type"]))
    permission.objects.using(db_alias).bulk_create(permission_list)


def update_client_group_permissions(apps, schema_editor):
    """
    Add 'Can add Request' and 'Can view Request'  permissions for 'Client' group
    """
    permission_model = apps.get_model('auth', 'Permission')
    group_model = apps.get_model("auth", "Group")
    
    db_alias = schema_editor.connection.alias
    
    group, _ = group_model.objects.using(db_alias).get_or_create(name="Client")
    group.permissions.add(permission_model.objects.using(db_alias).get(codename='add_request'))
    group.permissions.add(permission_model.objects.using(db_alias).get(codename='view_request'))


